class SocketManager {

	constructor(engine) {
		this.engine = engine;
		this.isRoomAdmin = false;
		//this.engine.playerUID = Utils.getRandomInt(0, 1000000);

		const firebaseConfig = {
			apiKey: "AIzaSyAlO9Z_pb-39T6meLw5NW5ONYbOqHxSXkg",
			authDomain: "webgl-game-64e00.firebaseapp.com",
			databaseURL: "https://webgl-game-64e00-default-rtdb.europe-west1.firebasedatabase.app",
			projectId: "webgl-game-64e00",
			storageBucket: "webgl-game-64e00.appspot.com",
			messagingSenderId: "391433195866",
			appId: "1:391433195866:web:2df8bb7f39fb8d07306363"
		};
		let firebaseApp = initializeApp(firebaseConfig);
		this.database = getDatabase(firebaseApp);
	}

	connect() {
		var scope = this;
		set(ref(this.database, 'users/' + this.engine.playerUID), this.engine.playerData)
			.then(function () { scope.onConnectionSucceed(); })
			.catch(function (error) { scope.onConnectionError(error); });
		/*firebase.database().ref('users/' + this.engine.playerUID).set(this.engine.playerData)
			.then(function () { scope.onConnectionSucceed(); })
			.catch(function (error) { scope.onConnectionError(error); });*/
	}

	onConnectionSucceed() {
		console.log('Firebase synchronization succeeded!');

		var scope = this;
		this.connected = true;

		var myConnectionsRef = ref(this.database, 'users/' + this.engine.playerUID); // We're connected (or reconnected)
		var connectedRef = ref(this.database, '.info/connected');
		onValue(connectedRef, function (snap) {
			if (snap.val() === true) {
				// When I disconnect, remove this device
				onDisconnect(myConnectionsRef).remove().catch((err) => {
					if (err) {
						console.error("could not establish onDisconnect event", err);
					}
				});
			} else {
				scope.connected = false;
			}
		}, { onlyOnce: true });

		scope.initListeners();
	}

	onConnectionError(error) {
		console.log('Firebase synchronization failed!', error);
	}

	initListeners() {
		var scope = this;

		// Listens for users to join/leave the server...
		this.engine.status = 'usersList';
		this.engine.UIManager.html.usersListWrap.classList.add('show');

		onChildAdded(ref(this.database, 'users'), (data) => {
			let addedUser = data.val();
			console.log('onChildAdded', addedUser);
			this.engine.UIManager.addUserToList(addedUser);
		});
		onChildRemoved(ref(this.database, 'users'), (data) => {
			let removedUser = data.val();
			//console.log('onChildRemoved', removedUser);
			this.engine.UIManager.removeUserFromList(removedUser);
		});
		onChildChanged(ref(this.database, 'users'), (data) => {
			let changedUser = data.val();
			//console.log("onChildChanged", changedUser, scope.engine.status);

			if (scope.engine.status != 'playing') {

				if (changedUser.ready && !changedUser.playing) {
					scope.engine.UIManager.setUserReady(changedUser);
					scope.checkUserStatuses();
				}

			} else {

				if (changedUser.username != scope.engine.playerUID) {
					let model = scope.engine.assetManager.characters.get(changedUser.username);
					if (model) {
						scope.engine.physicsManager.updateOtherUserCharacter(model, changedUser);
						scope.engine.controlsManager.updateCharacter(model, changedUser);
					}
				}

			}
		});
	}

	syncPlayer() {
		if (this.connected) {
			var scope = this;

			// THREE.Quaternion has underscore prefix for each component (_x, _y, _z, _w) but we want to send plain x,y,z,w on server
			let pos = { x: this.engine.player.position.x, y: this.engine.player.position.y, z: this.engine.player.position.z };
			let quat = { x: this.engine.player.quaternion.x, y: this.engine.player.quaternion.y, z: this.engine.player.quaternion.z, w: this.engine.player.quaternion.w };

			update(ref(this.database, 'users/' + this.engine.playerUID), {
				pos: pos,
				quat: quat,
				playing: true,
				controls: scope.engine.playerData.controls,
				animations: scope.engine.player.settings.animations
			});
		}
	}

	setPlayerReady() {
		//console.log("setPlayerReady");
		this.engine.status = 'waitingRoom';
		set(ref(this.database, 'users/' + this.engine.playerUID + '/ready'), true);
		/*firebase.database().ref('users/' + this.engine.playerUID + '/ready').set(true);*/
	}
	checkUserStatuses() {
		var ready = 0;
		this.engine.playersList.forEach(function (player, username) {
			if (player.ready) ready++;
		});
		//console.log("checkUserStatuses", this.engine.playersList, this.engine.playersList.size, ready);
		if (ready === this.engine.playersList.size) {
			console.log("initGame!");
			this.engine.initGame();
		}
	}

}

class GameEngine {

	constructor() {

		// Detects webgl
		if (!Detector.webgl) {
			Detector.addGetWebGLMessage();
			return;
		}

		this.clock = new Clock();
		this.playersList = new Map();
		this.player = null;
		this.status = 'off';
		this.debug = true;

		//

		this.playerData = {
			username: '',
			displayName: '',
			character: null,
			ready: false,
			playing: false,
			isRoomAdmin: false,
			userIndex: 0,
			pos: { x: 0, y: 0, z: 0 },
			quat: { x: 0, y: 0, z: 0, w: 0 },
			settings: null,
			controls: {
				freeze: false, // while true cannot be assigned new actions to the model
				status: 'run', // walk | run
				move: null, // null | forward | backward
				turn: null, // null | left | right
				jump: false, // false | true
				attacking: false, // false | true
				action: null // set dinamically
			}
		};

		//

		this.physicsManager = new PhysicsManager(this);
		this.assetManager = new AssetManager(this);
		this.controlsManager = new ControlsManager(this);
		this.UIManager = new UIManager(this);
		this.renderingManager = new RenderingManager(this);
		this.socketManager = new SocketManager(this);

	}

	init(userSelection) {

		var scope = this;
		this.status = 'userSelection';
		this.place = userSelection.place;
		this.playerUID = userSelection.username;
		this.playerData.username = userSelection.username;
		this.playerData.displayName = userSelection.displayName;
		this.playerData.character = userSelection.character;

		this.UIManager.init();

		fetch('json/assets.json').then(response => {

			return response.json();

		}).then(json => {

			scope.gameAssetsInfo = json;
			scope.socketManager.connect();

		});

		return this;

	}

	initGame() {
		this.status = 'playing';

		//console.log(this.UIManager.html.usersListWrap, this.UIManager.html.usersListWrap.classList.remove("show"));
		this.UIManager.html.usersListWrap.classList.remove("show");
		this.UIManager.html.loadingScreen.classList.add("show");

		window.addEventListener('resize', this.UIManager._onWindowResize.bind(this.UIManager), false);

		this.initManagers();
	}

	initManagers() {
		this._log("Initializing managers...");

		this.renderingManager.init();
		this.physicsManager.init();
		this.assetManager.init().then(() => {

			this.initObjects();
			this.initLevel();
			this.UIManager.init();
			this.controlsManager.init();
			this.renderingManager.initPostProcessing();
			this.UIManager.showGameInterface();

			console.log("backgroundSound", this.backgroundSound);
			this.backgroundSound.play();

			this.animate();

		});

	}

	initLevel() {
		this._log("Initializing level...");

		var geometry = new BoxBufferGeometry(1, 3.3, 1);
		var material = new MeshBasicMaterial({ color: 0x00ff00 });
		var cube = new Mesh(geometry, material);
		//this.renderingManager.scene.add( cube );
		cube.position.set(2, 2.65, 2);

		this.renderingManager.initLights();
		this.renderingManager.initEnvironment();
	}

	initObjects() {
		this._log("Initializing objects...");

		this.assetManager.objects.forEach(function (obj, username) {
			if (obj.status == 'hidden') obj.visible = false;
			this.renderingManager.scene.add(obj);
		}, this);
		this.assetManager.characters.forEach(function (obj, username) {
			if (obj.player === true) this.player = obj;
			if (obj.status == 'hidden') obj.visible = false;
			this.renderingManager.scene.add(obj);
		}, this);
	}

	animate() {

		window.requestAnimationFrame(this.animate.bind(this));

		const delta = this.clock.getDelta();

		this.UIManager.html.debugger.innerHTML = "";

		if (!this.UIManager.preloading) {
			this.physicsManager.update(delta);
			this.assetManager.update(delta);
			this.UIManager.update(delta);
		}

		if (this.controlsManager.controls.isLocked) this.controlsManager.update(delta);

		this.renderingManager.update(delta);

		if (this.UIManager.preloading) this.UIManager.hidePreloader();

	}

	_log(string) {
		if (this.preloading) this.engine.UIManager.updatePreloader(string);
		if (this.debug) console.log(string);
	}

}

